import React, {Component } from 'react';
import JSONdata from "../data.json";
import { Card } from "components/Card/Card.jsx";
import ApexCharts from '../variables/LineChart.js';

var labels= JSONdata.US_Week_time.xlabel;
var series= JSONdata.US_Week_time.series;
var legend_name=JSONdata.US_Week_time.series_label;

class Chart extends Component {
    createLegend(json) {
      var legend = [];
      for (var i = 0; i < json["legend_name"].length; i++) {
        var type = "fa fa-circle text-" + json["types"][i];
        legend.push(<i className={type} key={i} />);
        legend.push(" ");
        legend.push(json["legend_name"][i]);
      }
      return legend;
    }
    constructor(props) {
        super(props);
        this.state = {  }
    }



    render() { 


        var allData = [];
        // legend_name.forEach(function(name, i){
        //   var dataset={};
        //   dataset["names"]=name;
        //   var data={};
        //   labels.forEach((label, j) => data[label] = series[i][j]);
        //   dataset["data"]=data;
        //   allData.push(dataset);
        // });
        legend_name.forEach(function(name, i){
          var dataset={};
          dataset["names"]=name;
          var data=series[i]
          dataset["data"]=data;
          allData.push(dataset);
        });       

        var dataCollectTime=JSONdata.Date;
        var types= ["info", "danger", "warning"];

        var legend={legend_name, types};
        return (
            <Card
            statsIcon="fa fa-history"
            id="chartHours"
            title="Weekly Total Cases in US"
            category={"Updated by "+dataCollectTime}
            content={
              <div className="ct-chart">

                <ApexCharts />
                <br></br>
              </div>
            }
            // legend={
            //   <div className="legend">{this.createLegend(legend)}</div>
            // }
          />
        );
    }
}

export default Chart;
