import React, {Component } from 'react';
import JSONdata from "./data.json";
import ChartistGraph from "react-chartist";
import { Card } from "components/Card/Card.jsx";


class Chart extends Component {
    createLegend(json) {
      var legend = [];
      for (var i = 0; i < json.length; i++) {
        //var type = "fa fa-circle text-" + json["types"][i];
        //legend.push(<i className={type} key={i} />);
        legend.push(" ");
        legend.push(json[i]);
      }
      return legend;
    }
    constructor(props) {
        super(props);
        this.state = {  }
    }
    render() { 
        var labels= JSONdata.US_Week_time.xlabel;
        var series= JSONdata.US_Week_time.series;  
        var data={labels, series};
        var dataCollectTime=JSONdata.Date;
        var legend=JSONdata.US_Week_time.series_label;
        return (
            <Card
            statsIcon="fa fa-history"
            id="chartHours"
            title="Total Cases in US"
            category="Daliy updates"
            stats={"Updated by "+dataCollectTime}
            content={
              <div className="ct-chart">
                <ChartistGraph
                  data={data}
                  type="Line"
                />
              </div>
            }
            legend={
              <div className="legend">{this.createLegend(legend)}</div>
            }
          />
        );
    }
}

export default Chart;
